    $(function () {
        $('.touch-up').on('click', function () {
            var s = parseInt($(this).parent().siblings('input').val()) + 1;
            $(this).parent().siblings('input').val(s > 50 ? 50 : s);
        })
        $('.touch-down').on('click', function () {
            var s = parseInt($(this).parent().siblings('input').val()) - 1;
            $(this).parent().siblings('input').val(s <= 0 ? 1 : s);
        })
        $('.update').hide();
    })
