    function IngredientDownFunction(element) {
        var input = $(element).parent().siblings('.quantity-counter');
        var itemCount = $(element).parent().siblings('.quantity-counter').val();
        var itemCount = parseInt(itemCount);
        itemCount -= 1;
        if (itemCount >= 0) {
